    private void NextPizzaButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_NextPizzaButtonActionPerformed
        this.pos += 1;
        if(this.pos <= (order.numOfPizzas()-1)){
            jTextArea1.setText((order.getPizza(pos).getInvoice()));
            updateBoxes();  
        }else if(this.pos > order.numOfPizzas()-1){
            this.pos -= 1;
            jTextArea1.setText((order.getPizza(pos).getInvoice()));
            updateBoxes(); 
        }
        Counter.setText(String.valueOf(this.pos+1));
    }//GEN-LAST:event_NextPizzaButtonActionPerformed
    
    public void updateBoxes(){
        Pizza temp = order.getPizza(pos);
        SizeComboBox1.setSelectedItem(temp.getSize());
        CrustComboBox.setSelectedItem(temp.getCrust());
        SauceComboBox.setSelectedItem(temp.getSauce());
        T1ComboBox.setSelectedItem(temp.getTopping1());
        T2ComboBox.setSelectedItem(temp.getTopping2());
    }
    
    private void OkButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OkButtonActionPerformed
        orderForm frame = new orderForm(this.order); 
        frame.setLocationRelativeTo(null);
        frame.setDefaultCloseOperation(orderForm.EXIT_ON_CLOSE); 
        frame.setVisible(true);
        dispose();
    }//GEN-LAST:event_OkButtonActionPerformed

    private void PrevPizzaButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PrevPizzaButtonActionPerformed
        this.pos -= 1;
        if(this.pos >= 0){
            jTextArea1.setText((order.getPizza(pos).getInvoice()));
            updateBoxes();  
        }else{
            this.pos += 1;
            jTextArea1.setText((order.getPizza(pos).getInvoice()));
            updateBoxes(); 
        }
        Counter.setText(String.valueOf(this.pos+1));
    }//GEN-LAST:event_PrevPizzaButtonActionPerformed
    
    private void update(){
        order.updatePizza(
                SizeComboBox1.getSelectedItem().toString(), 
                CrustComboBox.getSelectedItem().toString(),
                SauceComboBox.getSelectedItem().toString(),
                T1ComboBox.getSelectedItem().toString(),
                T2ComboBox.getSelectedItem().toString(),
                this.pos
                );
        jTextArea1.setText((order.getPizza(pos).getInvoice()));
    }
    
    private void SizeComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SizeComboBox1ActionPerformed
        update();
    }//GEN-LAST:event_SizeComboBox1ActionPerformed

