    private void DeleteButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DeleteButtonActionPerformed
        order.deletePizza(this.pos);
        this.pos -= 1;
        if (this.pos < 0){
            orderForm frame = new orderForm(this.order); 
            frame.setLocationRelativeTo(null);
            frame.setDefaultCloseOperation(orderForm.EXIT_ON_CLOSE); 
            frame.setVisible(true);
            dispose();
        }else{
            jTextArea1.setText((order.getPizza(pos).getInvoice()));
            updateBoxes();
            Counter.setText(String.valueOf(this.pos+1));
        }
    }//GEN-LAST:event_DeleteButtonActionPerformed

